import React, { useEffect } from "react";

import './tickets-list.scss'

import { fetchData } from "../../store/asyncActions/search-id";

import { useState } from "react";

import Ticket from '../Ticket';
import { useDispatch, useSelector } from "react-redux";

export default function TicketsList() {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData())
  }, []);

  const [ num, setNum ] = useState(5);

  const tickets = useSelector(state => state.data.data)

  const filters = useS

  const loadMore = () => {
    if (tickets.length >= 5)
      setNum(num => num+5)
  }

  const ticketsList = () => {
    let i = 0;
    return tickets.map(ticket => {
      if (i < num) {
        i++;
        return <Ticket key={`${ticket.price}${ticket.carrier}${ticket.segments[0]}${ticket.segments[1]}`} props = {ticket}/>
      } 
      return null;
    })
  }

  return (
    <div className="tickets-list">
      {ticketsList()}
      <button type="button" onClick={() => loadMore()}>ПОКАЗАТЬ ЕЩЕ 5 БИЛЕТОВ!</button>
    </div>
  )
}
