import React, { useState } from "react";
import './filter.scss';

import { useDispatch, useSelector } from "react-redux";
import { setFilter } from "../../store/filterSlice";

import checkedLogo from './checkedLogo.svg';
import uncheckedLogo from './uncheckedLogo.svg'

export default function Filter({text}) {
  const [ checked, setChecked  ] = useState(useSelector(state => state.filter[text]));

  const dispatch = useDispatch();

  const handleCheck = () => {
    const id = text;
    dispatch(setFilter({id}));
    // eslint-disable-next-line react-hooks/rules-of-hooks
    setChecked(useSelector(state => state.filter[text]));
  };

  return (
    <div className="filter-item">
      <label onClick={() => handleCheck()}>
        <input type="checkbox" />
        <span>
          <img src={checked === true ? checkedLogo : uncheckedLogo} alt='checkbox'/>
        </span>
      </label>
      <div>{text}</div>
    </div>
  );
}
