import { getTicketsData } from "../actions"

const fetchSearchId = async () => {
    const response = await fetch('https://aviasales-test-api.kata.academy/search')
    const res = await response.json()
    return res.searchId
}

export const fetchData = () => {
  return dispatch => {
    fetchSearchId()
      .then(indexId => {
      console.log(indexId);
      while (true) {
        fetch(`https://aviasales-test-api.kata.academy/tickets?searchId=${indexId}`)
          .then(data => {
            console.log(data.status)
            return data.json()
          })
          .then(tickets => {
            console.log(tickets)
            dispatch(getTicketsData(tickets))
          })
      }
    })
  }
}
