import { createSlice } from "@reduxjs/toolkit";

const sortReducer = createSlice({
  name: 'sortState',
  initialState: {
    sortState: 'tab1',
  },
  reducers: {
    changeSortState(state, action) {
      state.sortState = action.payload.value;
    }
  }
})

export const { changeSortState } = sortReducer.actions;

export default sortSlice;
