import { getTicketsData } from "../actions"

const fetchSearchId = () => {
  return dispatch => {
    fetch('https://aviasales-test-api.kata.academy/search')
      .then(response => response.json())
      .then(res => {
        console.log('id: ', res.searchId);
        return fetch(`https://aviasales-test-api.kata.academy/tickets?searchId=${res.searchId}`)
      })
      .then(data => data.json())
      .then(json => {
        console.log(json)
        return json
      })
      .then(tickets => {
        console.log(tickets)
        dispatch(getTicketsData(tickets))
      })
  }
}

export const fetchData = () => {
  return dispatch => {
    fetchSearchId(indexId => {
      console.log(indexId);
      fetch()
    })
  }
}
