import React, { useState } from "react";
import './filter.scss';

import { useDispatch } from "react-redux";
import { setFilter } from "../../store/filterSlice";

import checkedLogo from './checkedLogo.svg';
import uncheckedLogo from './uncheckedLogo.svg'

export default function Filter({id}) {

  const dispatch = useDispatch();

  const handleCheck = () => {
    dispatch(setFilter({id}));
  };

  const filterName = () => {
    switch (id) {
      case 'all': return 'Все';
      case '0': return 'Без пересадок';
      case '1': return '1 пересадка';
      case '2': return '2 пересадки';
      case '3': return '3 пересадки';
      default: return '';
    }
  }

  return (
    <div className="filter-item">
      <label>
        <input id={id} type="checkbox" onClick={() => handleCheck()} />
        <span>
          <img src={checked === true ? checkedLogo : uncheckedLogo} alt='checkbox'/>
        </span>
      </label>
      <div>{filterName()}</div>
    </div>
  );
}
