import React, { useEffect } from "react";

import './tickets-list.scss'

import { fetchData } from "../../store/asyncActions/search-id";

import { useState } from "react";

import Ticket from '../Ticket';
import { useDispatch, useSelector } from "react-redux";

export default function TicketsList() {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData())
  }, []);

  const [ num, setNum ] = useState(5);

  const filters = useSelector(state => state.filter);

  const filtersArr = () => {
    let arr = [];
    for (let key in filters) {
      if (filters[key]) {
        arr.push(key)
      }
    }
    return arr;
  }

  const sort = useSelector(state => state.sort.sortState);

  const tickets = useSelector(state => state.data.data);

  const loadMore = () => {
    if (tickets.length >= 5)
      setNum(num => num+5)
  }

  const filteredList = () => 
    tickets.filter(ticket => filtersArr().indexOf(ticket.segments[0].stops.length.toString()) !== -1 &&
    filtersArr().indexOf(ticket.segments[1].stops.length.toString()) !== -1); 

  const sortedList = () => {
    const list = filteredList();
    if ( sort === 'tab1')
      return list.sort((a, b) => a.price > b.price ? 1 : -1);
    else if (sort ===  'tab2') {
      return list.sort((a, b) => a.segments[0].duration > b.segments[0].duration ? 1 : -1);
    }
  }

  const ticketsList = () => {
    if (sortedList().length === 0) {
      return (
        <div className="ticket">
      <div className="top-info">
        <div className="price">
          <span className="price__amount">{`${priceStr()} P`}</span>
        </div>
        <div className="logo">
          <img src={`https://pics.avs.io/99/36/${carrier}.png`} alt="logo"/>
        </div>
      </div>
      <div className="bottom-info">
        <div className="oneway-info"> 
          <div className="oneway-info__arrive">
            <span className="info">{`${segments[0].origin} - ${segments[0].destination}`}</span>
            <span className="description">{arriveTime1()}</span>
          </div>
          <div className="oneway-info__time-onway">
            <span className="info">В ПУТИ</span>
            <span className="description">{timeOnWay(segments[0].duration)}</span>
          </div>
          <div className="oneway-info__transits">
            <span className="info">{stopsStr1()}</span>
            <span className="description">{`${segments[0].stops}`}</span>
          </div>
        </div>
        <div className="oneway-info"> 
          <div className="oneway-info__arrive">
            <span className="info">{`${segments[1].origin} - ${segments[1].destination}`}</span>
            <span className="description">{arriveTime2()}</span>
          </div>
          <div className="oneway-info__time-onway">
            <span className="info">В ПУТИ</span>
            <span className="description">{timeOnWay(segments[1].duration)}</span>
          </div>
          <div className="oneway-info__transits">
          <span className="info">{stopsStr2()}</span>
            <span className="description">{`${segments[1].stops}`}</span>
          </div>
        </div>
      </div>
    </div>
        );
    }
    let i = 0;
    return sortedList().map(ticket => {
      if (i < num) {
        i++;
        return <Ticket 
                key={`${ticket.price}${ticket.carrier}${ticket.segments[0].duration}${ticket.segments[1]}`} 
                props = {ticket}/>
      } 
      return null;
    })
  }

  return (
    <div className="tickets-list">
      {ticketsList()}
      <button type="button" onClick={() => loadMore()}>ПОКАЗАТЬ ЕЩЕ 5 БИЛЕТОВ!</button>
    </div>
  )
}
