import { getTicketsData } from "../actions"

const fetchSearchId = () => {
    return fetch('https://aviasales-test-api.kata.academy/search')
      .then(response => response.json())
      .then(res => res.indexId)
}

export const fetchData = () => {
  return dispatch => {
    fetchSearchId()
      .then(indexId => {
      console.log(indexId);
      fetch(`https://aviasales-test-api.kata.academy/tickets?searchId=${indexId}`)
        .then(data => data.json())
        .then(tickets => {
          console.log(tickets)
          dispatch(getTicketsData(tickets))
        })
    })
  }
}
