import React, { useEffect } from "react";

import './tickets-list.scss'

import { fetchData } from "../../store/asyncActions/search-id";

import { useState } from "react";

import Ticket from '../Ticket';
import { useDispatch, useSelector } from "react-redux";

export default function TicketsList() {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchData())
  }, []);

  const [ num, setNum ] = useState(5);

  const filters = useSelector(state => {
    let arr = [];
    for (let key in state.filter) {
      if (state.filter[key]) {
        arr.push(key)
      }
    }
    return arr;
  });

  const sort = useSelector(state => state.sort.sortState);

  const tickets = useSelector(state => state.data.data);

  const loadMore = () => {
    if (tickets.length >= 5)
      setNum(num => num+5)
  }

  const filteredList = () => 
    tickets.filter(ticket => filters.indexOf(ticket.segments[0].stops.length.toString()) !== -1 &&
    filters.indexOf(ticket.segments[1].stops.length.toString()) !== -1); 

  const sortedList = () => {
    const list = filteredList();
    if ( sort === 'tab1')
      return list.sort((a, b) => a.price > b.price ? 1 : -1);
    else if (sort ===  'tab2') {
      return list.sort((a, b) => a.segments > b.duration ? 1 : -1);
    }
  }

  const ticketsList = () => {
    let i = 0;
    return sortedList().map(ticket => {
      if (i < num) {
        i++;
        return <Ticket 
                key={`${ticket.price}${ticket.carrier}${ticket.segments[0]}${ticket.segments[1]}`} 
                props = {ticket}/>
      } 
      return null;
    })
  }

  return (
    <div className="tickets-list">
      {ticketsList()}
      <button type="button" onClick={() => loadMore()}>ПОКАЗАТЬ ЕЩЕ 5 БИЛЕТОВ!</button>
    </div>
  )
}
