const initialState = {
  'all': true,
  '0': false,
  '1': true,
  '2': true,
  '3': true,
}

export const filterReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'changeState':
      const prevState = state[action.payload.id];
      const key = action.payload.id;
      console.log(prevState)
      return {...state, [key]: !prevState}
    default:
      return state;
  }
}
